import React from 'react';
import { Typography, Paper, Box, Avatar, useTheme, useMediaQuery } from '@mui/material';
import { motion } from 'framer-motion';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';
import StarIcon from '@mui/icons-material/Star';
import StarHalfIcon from '@mui/icons-material/StarHalf';
import StarBorderIcon from '@mui/icons-material/StarBorder';

interface TestimonialsProps {
  themeMode?: 'light' | 'dark';
}

const Testimonials: React.FC<TestimonialsProps> = ({ themeMode = 'light' }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const testimonials = [
    {
      name: 'John Smith',
      position: 'Senior Developer at TechCorp',
      image: 'https://via.placeholder.com/150',
      text: "Amithraj's technical expertise and ability to quickly adapt to new technologies impressed us all. He's a fast learner and a great team player.",
      rating: 5
    },
    {
      name: 'Sarah Johnson',
      position: 'Project Manager at InnovateTech',
      image: 'https://via.placeholder.com/150',
      text: "Working with Amithraj was a pleasure. His attention to detail and problem-solving skills helped us deliver the project ahead of schedule.",
      rating: 5
    },
    {
      name: 'Mike Williams',
      position: 'CTO at StartupX',
      image: 'https://via.placeholder.com/150',
      text: "Amithraj's contributions to our development team were invaluable. His code quality and documentation standards are exceptional.",
      rating: 5
    }
  ];

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <StarIcon key={`star-${i}`} sx={{ color: theme.palette.warning.main }} />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <StarHalfIcon key="half-star" sx={{ color: theme.palette.warning.main }} />
      );
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <StarBorderIcon key={`empty-star-${i}`} sx={{ color: theme.palette.warning.main }} />
      );
    }

    return stars;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          mb: 4,
          background: themeMode === 'light'
            ? 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.85) 100%)'
            : 'linear-gradient(135deg, rgba(30,30,30,0.95) 0%, rgba(30,30,30,0.85) 100%)',
          backdropFilter: 'blur(10px)',
          borderRadius: '24px',
          border: `1px solid ${themeMode === 'light' ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)'}`,
        }}
      >
        <Typography 
          variant="h4" 
          gutterBottom
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 700,
            mb: 4,
            textAlign: 'center',
            fontSize: isMobile ? '2rem' : '2.5rem',
          }}
        >
          Testimonials
        </Typography>

        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
          {testimonials.map((testimonial, index) => (
            <Paper
              key={index}
              elevation={2}
              component={motion.div}
              whileHover={{ scale: 1.02 }}
              sx={{
                p: 3,
                background: themeMode === 'light'
                  ? 'rgba(255,255,255,0.7)'
                  : 'rgba(40,40,40,0.7)',
                borderRadius: '16px',
                border: `1px solid ${themeMode === 'light' ? 'rgba(0,0,0,0.1)' : 'rgba(255,255,255,0.1)'}`,
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <Avatar 
                  src={testimonial.image} 
                  sx={{ 
                    width: 60, 
                    height: 60,
                    border: `2px solid ${theme.palette.primary.main}`,
                  }} 
                />
                <Box>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.primary' : 'text.secondary',
                      fontWeight: 600,
                    }}
                  >
                    {testimonial.name}
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                    }}
                  >
                    {testimonial.position}
                  </Typography>
                </Box>
              </Box>
              <Box sx={{ display: 'flex', gap: 0.5, mb: 2 }}>
                {[...Array(testimonial.rating)].map((_, i) => (
                  <StarIcon 
                    key={i} 
                    sx={{ 
                      color: theme.palette.primary.main,
                      fontSize: '1.2rem',
                    }} 
                  />
                ))}
              </Box>
              <Box sx={{ position: 'relative' }}>
                <FormatQuoteIcon 
                  sx={{ 
                    position: 'absolute',
                    top: -20,
                    left: -10,
                    color: theme.palette.primary.main,
                    opacity: 0.2,
                    fontSize: '3rem',
                  }} 
                />
                <Typography 
                  variant="body1" 
                  sx={{ 
                    color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                    lineHeight: 1.7,
                    pl: 2,
                  }}
                >
                  {testimonial.text}
                </Typography>
              </Box>
            </Paper>
          ))}
        </Box>
      </Paper>
    </motion.div>
  );
};

export default Testimonials;
