import React from 'react';
import { Typography, Paper, Box, Avatar, useTheme } from '@mui/material';
import { motion } from 'framer-motion';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';

const testimonials = [
  {
    name: 'John Doe',
    position: 'Senior Developer at Tech Corp',
    image: 'https://via.placeholder.com/100',
    text: 'Amithraj is an exceptional developer with a keen eye for detail. His problem-solving skills and dedication to writing clean, maintainable code make him a valuable asset to any team.',
  },
  {
    name: 'Jane Smith',
    position: 'Project Manager at Innovate Solutions',
    image: 'https://via.placeholder.com/100',
    text: 'Working with Amithraj was a pleasure. He consistently delivered high-quality work on time and was always willing to go the extra mile to ensure project success.',
  },
  {
    name: 'Mike Johnson',
    position: 'CTO at StartupX',
    image: 'https://via.placeholder.com/100',
    text: "Amithraj's technical expertise and ability to quickly adapt to new technologies impressed us all. He's a fast learner and a great team player.",
  },
];

const Testimonials: React.FC = () => {
  const theme = useTheme();

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          mb: 4,
          background: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.7) 100%)',
          backdropFilter: 'blur(10px)',
        }}
      >
        <Typography 
          variant="h4" 
          gutterBottom
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 700,
            mb: 4,
            textAlign: 'center',
          }}
        >
          What People Say
        </Typography>

        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 4, justifyContent: 'center' }}>
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.02 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <Paper
                sx={{
                  p: 3,
                  maxWidth: 350,
                  background: 'rgba(255,255,255,0.5)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 2,
                }}
              >
                <FormatQuoteIcon
                  sx={{
                    fontSize: 40,
                    color: theme.palette.primary.main,
                    opacity: 0.2,
                    mb: 2,
                  }}
                />
                <Typography variant="body1" paragraph sx={{ mb: 3 }}>
                  {testimonial.text}
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Avatar
                    src={testimonial.image}
                    sx={{ width: 60, height: 60 }}
                  />
                  <Box>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {testimonial.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {testimonial.position}
                    </Typography>
                  </Box>
                </Box>
              </Paper>
            </motion.div>
          ))}
        </Box>
      </Paper>
    </motion.div>
  );
};

export default Testimonials;
