import React from 'react';
import { Typography, Paper, Box, useTheme, useMediaQuery } from '@mui/material';
import { motion } from 'framer-motion';
import WorkIcon from '@mui/icons-material/Work';
import SchoolIcon from '@mui/icons-material/School';

interface ExperienceProps {
  themeMode: 'light' | 'dark';
}

const Experience: React.FC<ExperienceProps> = ({ themeMode }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const experiences = [
    {
      title: 'Senior Software Engineer',
      company: 'TechCorp',
      period: '2021 - Present',
      description: 'Leading the development of enterprise-level web applications using React, Node.js, and AWS.',
      icon: <WorkIcon />,
    },
    {
      title: 'Software Engineer',
      company: 'InnovateTech',
      period: '2019 - 2021',
      description: 'Developed and maintained multiple web applications, focusing on user experience and performance.',
      icon: <WorkIcon />,
    },
    {
      title: 'Junior Developer',
      company: 'StartupX',
      period: '2018 - 2019',
      description: 'Worked on frontend development and contributed to the company\'s main product.',
      icon: <WorkIcon />,
    },
  ];

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          mb: 4,
          background: themeMode === 'light'
            ? 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.85) 100%)'
            : 'linear-gradient(135deg, rgba(30,30,30,0.95) 0%, rgba(30,30,30,0.85) 100%)',
          backdropFilter: 'blur(10px)',
          borderRadius: '24px',
          border: `1px solid ${themeMode === 'light' ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)'}`,
        }}
      >
        <Typography 
          variant="h4" 
          gutterBottom
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 700,
            mb: 4,
            textAlign: 'center',
            fontSize: isMobile ? '2rem' : '2.5rem',
          }}
        >
          Experience
        </Typography>

        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          {experiences.map((experience, index) => (
            <Paper
              key={index}
              elevation={2}
              component={motion.div}
              whileHover={{ scale: 1.02 }}
              sx={{
                p: 3,
                background: themeMode === 'light'
                  ? 'rgba(255,255,255,0.7)'
                  : 'rgba(40,40,40,0.7)',
                borderRadius: '16px',
                border: `1px solid ${themeMode === 'light' ? 'rgba(0,0,0,0.1)' : 'rgba(255,255,255,0.1)'}`,
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 2 }}>
                <Box
                  sx={{
                    p: 1.5,
                    borderRadius: '12px',
                    background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                    color: 'white',
                  }}
                >
                  {experience.icon}
                </Box>
                <Box>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.primary' : 'text.secondary',
                      fontWeight: 600,
                      mb: 0.5,
                    }}
                  >
                    {experience.title}
                  </Typography>
                  <Typography 
                    variant="subtitle1" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                      mb: 1,
                    }}
                  >
                    {experience.company} • {experience.period}
                  </Typography>
                  <Typography 
                    variant="body1" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                      lineHeight: 1.7,
                    }}
                  >
                    {experience.description}
                  </Typography>
                </Box>
              </Box>
            </Paper>
          ))}
        </Box>
      </Paper>
    </motion.div>
  );
};

export default Experience;
