import React from 'react';
import { Typography, Paper, Box, Avatar, useTheme, useMediaQuery } from '@mui/material';
import { motion } from 'framer-motion';
import StarIcon from '@mui/icons-material/Star';
import StarHalfIcon from '@mui/icons-material/StarHalf';
import StarBorderIcon from '@mui/icons-material/StarBorder';

interface TestimonialsProps {
  themeMode: 'light' | 'dark';
}

const Testimonials: React.FC<TestimonialsProps> = ({ themeMode }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const testimonials = [
    {
      name: 'Sarah Johnson',
      role: 'Product Manager',
      company: 'TechCorp',
      avatar: 'SJ',
      rating: 5,
      text: 'Amithraj is an exceptional developer who consistently delivers high-quality work. His attention to detail and problem-solving skills are outstanding.',
    },
    {
      name: 'Mike Chen',
      role: 'Senior Developer',
      company: 'InnovateTech',
      avatar: 'MC',
      rating: 4.5,
      text: "Amithraj's technical expertise and ability to quickly adapt to new technologies impressed us all. He's a fast learner and a great team player.",
    },
    {
      name: 'Emily Davis',
      role: 'UX Designer',
      company: 'StartupX',
      avatar: 'ED',
      rating: 5,
      text: 'Working with Amithraj was a pleasure. He understands the importance of user experience and always delivers clean, efficient code.',
    },
  ];

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <StarIcon 
          key={`star-${i}`} 
          sx={{ 
            color: theme.palette.primary.main,
            fontSize: '1.2rem',
          }} 
        />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <StarHalfIcon 
          key="half-star" 
          sx={{ 
            color: theme.palette.primary.main,
            fontSize: '1.2rem',
          }} 
        />
      );
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <StarBorderIcon 
          key={`empty-star-${i}`} 
          sx={{ 
            color: theme.palette.primary.main,
            fontSize: '1.2rem',
          }} 
        />
      );
    }

    return stars;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          mb: 4,
          background: themeMode === 'light'
            ? 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.85) 100%)'
            : 'linear-gradient(135deg, rgba(30,30,30,0.95) 0%, rgba(30,30,30,0.85) 100%)',
          backdropFilter: 'blur(10px)',
          borderRadius: '24px',
          border: `1px solid ${themeMode === 'light' ? 'rgba(255,255,255,0.2)' : 'rgba(255,255,255,0.1)'}`,
        }}
      >
        <Typography 
          variant="h4" 
          gutterBottom
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 700,
            mb: 4,
            textAlign: 'center',
            fontSize: isMobile ? '2rem' : '2.5rem',
          }}
        >
          Testimonials
        </Typography>

        <Box sx={{ 
          display: 'grid', 
          gridTemplateColumns: isMobile ? '1fr' : 'repeat(3, 1fr)', 
          gap: 3 
        }}>
          {testimonials.map((testimonial, index) => (
            <Paper
              key={index}
              elevation={2}
              component={motion.div}
              whileHover={{ scale: 1.02 }}
              sx={{
                p: 3,
                background: themeMode === 'light'
                  ? 'rgba(255,255,255,0.7)'
                  : 'rgba(40,40,40,0.7)',
                borderRadius: '16px',
                border: `1px solid ${themeMode === 'light' ? 'rgba(0,0,0,0.1)' : 'rgba(255,255,255,0.1)'}`,
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                <Avatar
                  sx={{
                    width: 56,
                    height: 56,
                    background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                    fontSize: '1.5rem',
                    fontWeight: 600,
                  }}
                >
                  {testimonial.avatar}
                </Avatar>
                <Box>
                  <Typography 
                    variant="h6" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.primary' : 'text.secondary',
                      fontWeight: 600,
                    }}
                  >
                    {testimonial.name}
                  </Typography>
                  <Typography 
                    variant="subtitle2" 
                    sx={{ 
                      color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                    }}
                  >
                    {testimonial.role} at {testimonial.company}
                  </Typography>
                </Box>
              </Box>
              <Box sx={{ display: 'flex', gap: 0.5, mb: 2 }}>
                {renderStars(testimonial.rating)}
              </Box>
              <Typography 
                variant="body1" 
                sx={{ 
                  color: themeMode === 'light' ? 'text.secondary' : 'text.primary',
                  lineHeight: 1.7,
                }}
              >
                {testimonial.text}
              </Typography>
            </Paper>
          ))}
        </Box>
      </Paper>
    </motion.div>
  );
};

export default Testimonials;
