import React from 'react';
import { Typography, Paper, Box, Avatar, useTheme, useMediaQuery } from '@mui/material';
import { motion } from 'framer-motion';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';
import StarIcon from '@mui/icons-material/Star';
import StarHalfIcon from '@mui/icons-material/StarHalf';
import StarBorderIcon from '@mui/icons-material/StarBorder';

const testimonials = [
  {
    name: 'John Doe',
    position: 'Senior Developer at Tech Corp',
    image: 'https://via.placeholder.com/100',
    text: 'Amithraj is an exceptional developer with a keen eye for detail. His problem-solving skills and dedication to writing clean, maintainable code make him a valuable asset to any team.',
    rating: 5,
  },
  {
    name: 'Jane Smith',
    position: 'Project Manager at Innovate Solutions',
    image: 'https://via.placeholder.com/100',
    text: 'Working with Amithraj was a pleasure. He consistently delivered high-quality work on time and was always willing to go the extra mile to ensure project success.',
    rating: 4.5,
  },
  {
    name: 'Mike Johnson',
    position: 'CTO at StartupX',
    image: 'https://via.placeholder.com/100',
    text: "Amithraj's technical expertise and ability to quickly adapt to new technologies impressed us all. He's a fast learner and a great team player.",
    rating: 5,
  },
];

const Testimonials: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <StarIcon key={`star-${i}`} sx={{ color: theme.palette.warning.main }} />
      );
    }

    if (hasHalfStar) {
      stars.push(
        <StarHalfIcon key="half-star" sx={{ color: theme.palette.warning.main }} />
      );
    }

    const emptyStars = 5 - stars.length;
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <StarBorderIcon key={`empty-star-${i}`} sx={{ color: theme.palette.warning.main }} />
      );
    }

    return stars;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          mb: 4,
          background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.85) 100%)',
          backdropFilter: 'blur(10px)',
          borderRadius: '24px',
          border: '1px solid rgba(255,255,255,0.2)',
        }}
      >
        <Typography 
          variant="h4" 
          gutterBottom
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 700,
            mb: 4,
            textAlign: 'center',
            fontSize: isMobile ? '2rem' : '2.5rem',
          }}
        >
          What People Say
        </Typography>

        <Box sx={{ 
          display: 'grid', 
          gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: 4,
          justifyContent: 'center',
        }}>
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.02 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <Paper
                sx={{
                  p: 3,
                  height: '100%',
                  background: 'rgba(255,255,255,0.7)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: '20px',
                  border: '1px solid rgba(255,255,255,0.2)',
                  display: 'flex',
                  flexDirection: 'column',
                  gap: 2,
                }}
              >
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                  <FormatQuoteIcon
                    sx={{
                      fontSize: 40,
                      color: theme.palette.primary.main,
                      opacity: 0.2,
                    }}
                  />
                  <Box sx={{ display: 'flex', gap: 0.5 }}>
                    {renderStars(testimonial.rating)}
                  </Box>
                </Box>
                <Typography 
                  variant="body1" 
                  paragraph 
                  sx={{ 
                    mb: 3,
                    fontStyle: 'italic',
                    color: 'text.secondary',
                    flexGrow: 1,
                  }}
                >
                  {testimonial.text}
                </Typography>
                <Box sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: 2,
                  mt: 'auto',
                }}>
                  <Avatar
                    src={testimonial.image}
                    sx={{ 
                      width: 60, 
                      height: 60,
                      border: `2px solid ${theme.palette.primary.main}`,
                    }}
                  />
                  <Box>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {testimonial.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {testimonial.position}
                    </Typography>
                  </Box>
                </Box>
              </Paper>
            </motion.div>
          ))}
        </Box>
      </Paper>
    </motion.div>
  );
};

export default Testimonials;
