import React from 'react';
import { Box, Typography, Grid, Card, CardContent, useTheme, Paper } from '@mui/material';
import { motion } from 'framer-motion';

const Projects: React.FC = () => {
  const theme = useTheme();
  const projects = [
    {
      title: 'Veterinary Hospital Management System',
      description: 'Developed a web application for managing patient information and medical records for veterinary hospitals using Angular.'
    },
    {
      title: 'Restaurant One Solution (ROS)',
      description: 'Worked as a Data Analyst using Excel for organizing and visualizing restaurant management data, defining anomalies, and generating reports. Assisted in streamlining restaurant workflows.'
    },
    {
      title: 'Connect My Doctor (CMD)',
      description: 'Created and optimized web pages for a telehealth platform, enabling patients to book appointments based on symptoms and doctor availability.'
    },
    {
      title: 'Chat Application Integrated with E-commerce',
      description: 'Developed a chat-based web application that allows users to browse and purchase products while chatting with customer service. Integrated AI-powered bot chat for customer service.'
    },
    {
      title: 'Facebook Clone',
      description: 'Developed a social media platform resembling Facebook, allowing users to create profiles, post updates, like, comment, and share content. Integrated real-time notifications and chat features.'
    }
  ];

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Projects
      </Typography>
      <Typography variant="body1">
        Here you can showcase your projects.
      </Typography>
      <Grid container spacing={4}>
        {projects.map((project, index) => (
          <Grid item xs={12} md={6} key={index}>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <Card 
                sx={{ 
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'translateY(-8px)',
                    boxShadow: `0 12px 24px ${theme.palette.primary.light}33`,
                  }
                }}
              >
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography 
                    variant="h5" 
                    component="h3" 
                    gutterBottom
                    sx={{ 
                      color: theme.palette.primary.main,
                      fontWeight: 600
                    }}
                  >
                    {project.title}
                  </Typography>
                  <Typography 
                    variant="body1" 
                    color="text.secondary"
                    sx={{ 
                      lineHeight: 1.6,
                      fontSize: '1.1rem'
                    }}
                  >
                    {project.description}
                  </Typography>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default Projects;
